import React from "react";
import "./List.css";
import * as restaurantData from "./data/restaurant.json";

class List extends React.Component{

    moyenneAvis(restaurant) {
        console.log(restaurant.restaurantName);
        const totalStars = restaurant.ratings.reduce((previousValue, currentValue, index, array) => {
            console.log('index ', index );
            console.log('array.length ', array.length);
            console.log('condition ', index >= array.length);
            if(index +1 === array.length){
                return (previousValue.stars + currentValue.stars) / array.length;
            }

            return previousValue.stars + currentValue.stars;
        });
        console.log(totalStars);
    }

    render(){

        return(restaurantData ?
            <ul>
                {restaurantData.features.map(restaurant => (
                    <li id="first" key={restaurant.restaurantID} onClick={() => {
                        console.log("Selection du restaurant : " + restaurant.restaurantName);
                      }}>
                        <div>
                            <img className="imgRestaurant" src={restaurant.img} alt="Le restaurant"></img>
                        </div>
                        <div>
                            <h2>{restaurant.restaurantName}</h2>
                            <div className="moyenneAvis">{this.moyenneAvis(restaurant)}</div>
                            <div className="adresse">{restaurant.address}</div>
                        </div>
                    </li>
                ))}
            </ul>
        : <div>loading...</div>);
    }
}

export default List;
