import React, {useState} from "react";
//import "./List.css";
import "./Card_Details.css";
import * as restaurantData from "./data/restaurant.json";

const CardDetails = props => {

    const [restaurant, setRestaurant] = useState(props.restaurant);
    const [moyenneAvis, setMoyenneAvis] = useState(props.moyenneAvis);
    const [avis, setAvis] = useState(props.avis);

    return(
        <div className="details">
            <div className="detailsRestaurant">
                <img className="imgRestaurantDetails" src={restaurant.img} alt=""></img>
                <div className="infoRestaurantDetails">
                    <h2>{restaurant.restaurantName}</h2>
                    <div className="moyenneAvis">
                        <span className="moyenneAvisText">{moyenneAvis}</span>
                        <img className="etoileAvis" src="etoile.png" alt="Etoile des avis"></img>
                    </div>
                    <div className="adresse">{restaurant.address}</div>
                </div>
            </div>
            <div className="note">
                <span>4</span>
                <img src="etoile.png" className="imgAvisDetails" alt="Etoile des avis"></img>
                <div className="avis">Un excellent restaurant, j'y reviendrai ! Par contre il vaut mieux aimer la viande. Un excellent restaurant, j'y reviendrai ! Par contre il vaut mieux aimer la viande.</div>
            </div>
            <div className="note">
                <span>5</span>
                <img src="etoile.png" className="imgAvisDetails" alt="Etoile des avis"></img>
                <div className="avis">Tout simplement mon restaurant préféré !</div>
            </div>
        </div>
    );
}

export default CardDetails;
