import React, {useState} from "react";
import "./List.css";
import Card from "./Card";

const List = props => {

    const [restaurantDisplay, setRestaurantDisplay] = useState(props.restaurantDisplay); // Erreur ! Ne prend pas la valeur dans le useState

    useEffect(() => {
        setRestaurantDisplay(props.restaurantDisplay);
        console.log("restaurant après : ", restaurantDisplay);
      },[restaurantDisplay])
    //console.log(props.restaurantDisplay);

    return(props.restaurantDisplay ?
        <ul className="list">
            {props.restaurantDisplay.map(restaurant => (
                restaurant.display = true,
                <Card key={restaurant.restaurantID} restaurant={restaurant}/>
            ))}
        </ul>
    : <div>Chargement des restaurants...</div>);
}

export default List;
