import React, {useState} from "react";
import "./Card.css";
import CardDetails from "./Card_Details";
import * as restaurantData from "./data/restaurant.json";

const Card = props => {

    const [display, setDisplay] = useState(props.display);
    const [displayDetails, setDisplayDetails] = useState(props.displayDetails);
    const [restaurant, setRestaurant] = useState(props.restaurant);
    var restaurantDisplay;

    const moyenneAvis = (restaurant) => {
        const totalStars = restaurant.ratings.map(rating => rating.stars).reduce((previousValue, currentValue, index, array) => {
            return index + 1 === array.length ? (previousValue + currentValue) / array.length : previousValue + currentValue;
        });
        return totalStars.toFixed(1);
    }

    const displayRestaurant = () => {
        if(display){
            restaurantDisplay = restaurantData.features.filter(feature => feature.ratings.stars == 5);
            console.log(restaurantDisplay);
            return(
            <li key={restaurant.restaurantID} className="itemList" onClick={() => setDisplayDetails(!displayDetails)}>
                {displayDetails ? 
                <CardDetails restaurant={restaurant} moyenneAvis={moyenneAvis(restaurant)} avis={restaurant.ratings}/> : 
                <div className="cardRestaurant">
                    <img className="imgRestaurant" src={restaurant.img} alt="Le restaurant"></img>
                    <div style={{marginTop: "auto", marginBottom: "auto"}}>
                        <h2>{restaurant.restaurantName}</h2>
                        <div className="moyenneAvis">
                            <span className="moyenneAvisText">{moyenneAvis(restaurant)}</span>
                            <img src="etoile.png" className="etoileAvis" alt="Etoile des avis"></img>
                        </div>
                        <div className="adresse">{restaurant.address}</div>
                    </div>
                </div>}
            </li>);
        }
    }

    return(
        <div className="card">
            {displayRestaurant()}
        </div>
    );
}

export default Card;
