import React, {useState} from "react";
import "./List.css";
import * as restaurantData from "./data/restaurant.json";
import Card from "./Card";

const List = props => {

    const moyenneAvis = (restaurant) => {
        console.log(restaurant);
        const totalStars = restaurant.ratings.map(rating => rating.stars).reduce((previousValue, currentValue, index, array) => {
            restaurant.average = index + 1 === array.length ? (previousValue + currentValue) / array.length : previousValue + currentValue;
            return restaurant.average;
        });
        return totalStars.toFixed(1);
    }

    var restaurantDisplay = restaurantData.features.filter(feature => console.log(feature) > '0');
    console.log(restaurantDisplay);

    return(restaurantData ?
        <ul className="list">
            {restaurantData.features.map(restaurant => (
                <Card display={true} displayDetails={false} restaurant={restaurant} />
            ))}
        </ul>
    : <div>Chargement des restaurants...</div>);
}

export default List;
