import React, {useState, useContext} from "react";
import "./List.css";
import Card from "./Card";
import RestaurantContext from "./RestaurantContext";

const List = props => {

    //const [restaurantDisplay, setRestaurantDisplay] = useState(props.restaurantDisplay); // Erreur ! Ne prend pas la valeur dans le useState
    const {restaurants, updateRestaurants} = useContext(RestaurantContext);

    return(restaurants ?
        <ul className="list">
            {restaurants.map(restaurant => (
                restaurant.display = true,
                <Card key={restaurant.restaurantID} restaurant={restaurant}/>
            ))}
        </ul>
    : <div>Chargement des restaurants...</div>);
}

export default List;
