import React, {useState, useEffect, useContext} from "react";
import "../style/Card.css";
import CardDetails from "./Card_Details";
import RestaurantContext from "./RestaurantContext";

const Card = props => {

    //const [restaurant, setRestaurant] = useState(props.restaurant);
    //const [displayDetails, setDisplayDetails] = useState(false);

    const {restaurants, updateRestaurants} = useContext(RestaurantContext);

    const selectOneRestaurant = () => {
        return restaurants.filter(restau => restau.restaurantID === props.restaurantID)[0];
    }

    var restaurant = selectOneRestaurant();

    // useEffect(() => {
    //     setDisplayDetails(displayDetails);
    //     console.log(displayDetails);
    // },[displayDetails])

    // useEffect(() => {
    //     setRestaurant(restaurant);
    //     console.log(restaurant);
    // },[restaurant])

    const changeDisplayDetails = () => {
        restaurant.displayDetails = !restaurant.displayDetails;
        var allRestaurants = restaurants;
        console.log("restaurant : ", restaurant);
        console.log("allRestaurants : ", allRestaurants);
        allRestaurants.splice(0, 1, restaurant);
        console.log("allRestaurants : ", allRestaurants);

    }

    const displayRestaurant = () => {
        if(restaurant.display){
            return(
            <li 
                key={restaurant.restaurantID} 
                className="itemList" 
                id={"restaurant-" + restaurant.restaurantID} 
                onClick={/*() => setDisplayDetails(!displayDetails)*/ changeDisplayDetails}>
                    {console.log("aaa"),
                    restaurant.displayDetails ?
                    <CardDetails restaurant={restaurant} moyenneAvis={restaurant.average} avis={restaurant.ratings}/> : 
                    <div className="cardRestaurant">
                        <img className="imgRestaurant" src={restaurant.img} alt="Le restaurant"></img>
                        <div style={{marginTop: "auto", marginBottom: "auto"}}>
                            <h2>{restaurant.restaurantName}</h2>
                            <div className="moyenneAvis">
                                <span className="moyenneAvisText">{restaurant.average}</span>
                                <img src="etoile.png" className="etoileAvis" alt="Etoile des avis"></img>
                            </div>
                            <div className="adresse">{restaurant.address}</div>
                        </div>
                    </div>}
            </li>);
        }
    }

    return(
        <div className="card">
            {displayRestaurant()}
        </div>
    );
}

export default Card;
