import React, {useState} from "react";
import "./List.css";
import * as restaurantData from "./data/restaurant.json";

const Card = props => {

    const moyenneAvis = (restaurant) => {
        const totalStars = restaurant.ratings.map(rating => rating.stars).reduce((previousValue, currentValue, index, array) => {
            return index + 1 === array.length ? (previousValue + currentValue) / array.length : previousValue + currentValue;
        });
        return totalStars.toFixed(1);
    }

    const clickOnRestaurant = (restaurantID) => {
        //console.log(restaurantData.restaurant.restaurantID[1]);

        var res = restaurantData.filter(restaurant => restaurant.restaurantID === '1')

        return(
            <div key={restaurantID}>
                <img className="imgRestaurant" alt="Le restaurant"></img>
                <div style={{marginTop: "auto", marginBottom: "auto"}}>
                    <h2></h2>
                </div>
            </div>
        )
    }

    const displayRestaurant = (display, displayDetails) => {
        if(display){
            if(displayDetails){
                return(
                <div key={props.restaurant.restaurantID}>
                    <img className="imgRestaurant" src={props.restaurant.img} alt="Le restaurant"></img>
                    <div style={{marginTop: "auto", marginBottom: "auto"}}>
                        <h2>{props.restaurant.restaurantName}</h2>
                        <div className="moyenneAvis">
                            <span style={{verticalAlign:'middle'}}>{moyenneAvis(props.restaurant)}</span>
                            <img src="etoile.png" className="imgAvis" alt="Etoile des avis"></img>
                        </div>
                        <div className="adresse">{props.restaurant.address}</div>
                    </div>
                </div>);
            } else {
                return(
                <li key={props.restaurant.restaurantID} className="itemList" onClick= {() => {
                    clickOnRestaurant(props.restaurant.restaurantID);
                    }}>
                    <img className="imgRestaurant" src={props.restaurant.img} alt="Le restaurant"></img>
                    <div style={{marginTop: "auto", marginBottom: "auto"}}>
                        <h2>{props.restaurant.restaurantName}</h2>
                        <div className="moyenneAvis">
                            <span style={{verticalAlign:'middle'}}>{moyenneAvis(props.restaurant)}</span>
                            <img src="etoile.png" className="imgAvis" alt="Etoile des avis"></img>
                        </div>
                        <div className="adresse">{props.restaurant.address}</div>
                    </div>
                </li>);
            }
        }
    }

    return(
        <div>
            {displayRestaurant(props.display, props.displayDetails)}
        </div>);
}

export default Card;
