import React, {useState} from "react";
import "./List.css";
import * as restaurantData from "./data/restaurant.json";

const CardDetails = props => {

    const [restaurant, setRestaurant] = useState(props.restaurant);
    const [moyenneAvis, setMoyenneAvis] = useState(props.moyenneAvis);

    return(
    //<div>{restaurant.restaurantName}</div>

    <div className="details">
        <div className="detailsRestaurant">
            <img className="imgRestaurant" src="logo_resto.png" alt=""></img>
            <div className="infoRestaurant">
                <h2>{restaurant.restaurantName}</h2>
                <div className="moyenneAvis">
                    <span>{moyenneAvis}</span>
                    <img src="etoile.png" className="etoileAvis" alt="Etoile des avis"></img>
                </div>
                <div className="adresse">{restaurant.address}</div>
            </div>
        </div>
        <div className="note">
            <span>4</span>
            <img src="etoile.png" className="imgAvis" alt="Etoile des avis"></img>
            <div className="avis">Un excellent restaurant, j'y reviendrai ! Par contre il vaut mieux aimer la viande.</div>
        </div>
        <div className="note">
            <span>5</span>
            <img src="etoile.png" className="imgAvis" alt="Etoile des avis"></img>
            <div className="avis">Tout simplement mon restaurant préféré !</div>
        </div>
    </div>
    );
}

export default CardDetails;
